{{
(function(){
	try{
		let rgx = new RegExp(smsRegex.text)
		const parts = rgx.exec(sampleSms.text)
		if (parts && parts.length > 0 ) {
			parts.shift()
			storeValue('groups', parts, false)
		} else {
			showAlert('Incorrect Regex')
		}
		rgx = null
		showAlert(`Total Groups ${parts.length.toString()}`)
	} catch (e) {
		showAlert(e.message)
	}
}
)()
}}

